function [xyY, XYZ] = Lab2xyY(Lab, wp_xyY, dim)
%2013dec02 * [cw] 

if nargin < 3
    dim = 2;
end

wp_XYZ = xyY2XYZ(wp_xyY,2);

XYZ = Lab2XYZ(Lab, wp_XYZ, dim);

