function Lab = xyY2Lab(xyY, wp_xyY, dim)
% 2013dec02 * [cw]
% 2014oct14 added default if nargin < 2 [cw]

if nargin < 3
    dim = 2;
end

XYZ = xyY2XYZ(xyY, dim);
wp_XYZ = xyY2XYZ(wp_xyY, 2);
